import React, {Fragment, useEffect} from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import {Link} from 'react-router-dom';
import Spinner from '../layout/Spinner';
import { getProfileById } from '../../actions/profile';
import  ProfileTop  from './ProfileTop';
import  ProfileAbout from './ProfileAbout';
import  ProfileExperience from './ProfileExperience';
import  ProfileEducation from './ProfileEducation';
import  ProfileGithub from './ProfileGithub';
import auth from '../../reducers/auth';


const Profile = ({ 
    getProfileById,
    profile:{profile,loading},
    auth,
    match })=> {
      
    useEffect(() =>{
        getProfileById(match.params.id);
    },[getProfileById,match.params.id]);


    


    return (
         
        <Fragment>
            {profile === null || loading ? (
                <Spinner /> 
                ):(
                 <Fragment>
              <Link to ='/profiles' className='btn btn-light'>
                  Back To Profile
              </Link>
              { auth.isAuthenticated && auth.loading===false && profile.user._id === auth.user._id && <Link to='/edit-profile' className='btn btn-dark'>
                  Edit Profile
              </Link>}
            </Fragment>
            )} 
            <div className='profile-grid my-1'>
                {profile!=null && <ProfileTop profile={profile}/>}
                {profile!=null && <ProfileAbout profile={profile}/>}
                <div className="profile-exp bg-white p-2">
                        <h2 className="text-primary">Experience</h2>
                       { profile!=null && profile.experience.length > 0 ? (
                           <Fragment>
                               {
                                   profile.experience.map(experience => (
                                       <ProfileExperience
                                         key={experience._id}
                                         experience={experience}
                                         />
                                   ))
                               }
                           </Fragment>
                       )
                       :(
                          <h4>No experience Credentials</h4>
                          )}
                 </div>
                
                 <div className="profile-edu bg-white p-2">
                        <h2 className="text-primary">Experience</h2>
                       { profile!=null && profile.education.length > 0 ? (
                           <Fragment>
                               {
                                   profile.education.map(education => (
                                       <ProfileEducation
                                         key={education._id}
                                         education={education}
                                         />
                                   ))
                               }
                           </Fragment>
                       )
                       :(
                          <h4>No education Credentials</h4>
                          )}
                 </div>
                 {
                     profile!=null && profile.githubusername && (
                         <ProfileGithub username={profile.githubusername}/>
                     )
                 }

            </div> 
            
        </Fragment>

    );
};

Profile.propTypes = {
    getProfileById:PropTypes.func.isRequired,
    profile:PropTypes.object.isRequired,
    auth:PropTypes.object.isRequired
};

const mapStateToProps = state => ({
    profile: state.profile,
    auth:state.auth
})

export default connect(mapStateToProps,{getProfileById})(Profile);
